        fetch(`/api/messages/${user}`)
            .then(response => response.json())
            .then(data => {
                setList(data.messages);
            })
            .catch(error => console.error('Error fetching messages:', error));
    }, [user]);  */

    const [list,setList] = useState([])
    console.log(list)

    const renderedList = list.map((message, index) => (
        <div key={index}>
            <p>From: {message.from}</p>
            <p>To: {message.to}</p>
            <p>Content: {message.content}</p>
            <p>Date: {new Date(message.dateInMs).toLocaleString()}</p>
            <hr />
        </div>
    ));

    
    return (
    <div className="bg-slate-500 h-screen flex">
        <div className="border border-black h-screen w-1/3">
            {renderedList}
        </div>
        <div className="border border-black h-screen w-2/3">  
        </div>
    </div>
        )
}
