import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export function Chat({ user }) {
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate("/");
    }

    fetch(`/api/messages/${user}`)
      .then((response) => response.json())
      .then((data) => {
        setList(data.messages);
      })
      .catch((error) => console.error("Error fetching messages:", error));
  }, [user]);

  //STATE
  const [list, setList] = useState([]);
  const [friend, setFriend] = useState("");
  const [message, setMessage] = useState("");
  console.log(list);
  console.log(friend);
  console.log(message);

  //HANDLERS
  const handleClick = (clUser) => {
    setFriend(clUser);
  };

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleMessageSubmit = (e) => {
    e.preventDefault();

    if (!friend || !message) {
      return;
    }

    const newMessage = {
      from: user,
      to: friend,
      content: message,
      dateInMs: Date.now(),
    };

    fetch(`/api/messages/${user}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newMessage),
    })
      .then((response) => response.json())
      .then((data) => {
        // Assuming the server returns the updated messages
        setList(data.messages);
      })
      .catch((error) => console.error("Error sending message:", error));

    setMessage("");
  };

  //LISTS
  const renderedUsers = list.map((user, i) => {
    return (
      <div key={i} onClick={() => handleClick(user.to)} className="border h-20">
        {user.to}
      </div>
    );
  });

  const filteredList = list.filter((element) => {
    return element.to !== friend
})

  const renderedMessages = list.map((element) => {
    return <div>{element.content}</div>;
  });


