})

  /*  const renderedList = list.map((message, index) => (
        <div key={index}>
            <p>From: {message.from}</p>
            <p>To: {message.to}</p>
            <p>Content: {message.content}</p>
            <p>Date: {new Date(message.dateInMs).toLocaleString()}</p>
            <hr />
        </div>
    )); */

  return (
    <div className="bg-slate-500 h-screen flex">
      <div className="border border-black h-screen w-1/3">{renderedUsers}</div>
      <div className="border border-black h-screen w-2/3">
        {renderedMessages}
        <div className="">
          <form onSubmit={handleMessageSubmit}>
            <input onChange={handleMessageChange} value={message} type="text" />
          </form>
        </div>
      </div>
    </div>
  );
}
