import { useHistory } from "react-router-dom";

export function Login({ user, setUser }) {
  const navigate = useHistory();

  const handleChange = (e) => {
    setUser(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Redirect to the chat page
    history.push("/chat");
  };

  return (
    <div className="bg-slate-500 h-screen flex items-center justify-center">
      <div className="border rounded border-white w-1/4 h-2/4 bg-slate-400">
        <div className="text-center">
          <h1 className="mt-12 mb-12 text-5xl">Login</h1>
          <form
            className="flex flex-col gap-2 items-center"
            onSubmit={handleSubmit}
          >
            <label className="">Username</label>
            <input
              onChange={handleChange}
              value={user}
              className="rounded pl-1 w-1/4"
              placeholder="Enter username"
              type="text"
            />
            <button type="submit" className="bg-blue-500 text-white rounded p-2">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
