import { useState,useEffect } from "react"
import { useNavigate } from "react-router-dom";



export function Chat ({user}) {
    const navigate = useNavigate();

    useEffect(() => {

        if(!user) {
            navigate("/")
        }

        fetch(`/api/messages/${user}`)
            .then(response => response.json())
            .then(data => {
                setList(data.messages);
            })
            .catch(error => console.error('Error fetching messages:', error));
    }, [user]); 

    const [list,setList] = useState([])
    const [friend,setFriend] = useState("")
    const [message,setMessage] = useState("")
    console.log(list)
    console.log(friend)

    const handleClick = (clUser) => {
        setFriend(clUser)
    }

    const renderedUsers = list.map((user,i) => {
        return <div key={i} onClick={() => handleClick(user.to)} className="border h-20">{user.to}</div>
    })
    const renderedMessages = list.map((element) => {
        return <div>{element.content}</div>
    })

    const renderedList = list.map((message, index) => (
        <div key={index}>
            <p>From: {message.from}</p>
            <p>To: {message.to}</p>
            <p>Content: {message.content}</p>
            <p>Date: {new Date(message.dateInMs).toLocaleString()}</p>
            <hr />
        </div>
    ));

    
    return (
    <div className="bg-slate-500 h-screen flex">
        <div className="border border-black h-screen w-1/3">
            {renderedUsers}
        </div>
        <div className="border border-black h-screen w-2/3">  
        {renderedMessages}
        <div className="">
        <form>
            <input value={message} type="text"/>
        </form>

        </div>
        </div>
    </div>
        )
}
